sys_variable_value.value:
(function execute(inputs, outputs) {
  outputs.facts = inputs.response.root_array.map(function(fact) {
    return {
      id: fact.a__id,
      text: fact.text,
      type: fact.type
    };
  });
})(inputs, outputs);

